import React, { useState, useContext } from 'react'
import { ThemeProvider } from '@material-ui/core/styles'
import { lightTheme, darkTheme, lightTheme_he, darkTheme_he } from '../ui/theme'

const ThemeUpdateContext = React.createContext()

export function useUpdateTheme() {
  return useContext(ThemeUpdateContext)
}

export function ThemeContextProvider({ children }) {
  const [isDarkTheme, setIsDarkTheme] = useState(false)
  const [currentTheme, setCurrentTheme] = useState(lightTheme)
  const themes = {
    he: {
      dark: darkTheme_he,
      light: lightTheme_he,
    },
    en: {
      dark: darkTheme,
      light: lightTheme,
    },
  }
  const toggleTheme = language => {
    setIsDarkTheme(!isDarkTheme)
  }
  const setTheme = (isDark, language) => {
    const dark = isDark ? 'dark' : 'light'
    setCurrentTheme(themes[language][dark])
  }

  return (
    <ThemeUpdateContext.Provider value={{ toggleTheme, isDarkTheme, setTheme }}>
      <ThemeProvider theme={currentTheme}>{children}</ThemeProvider>
    </ThemeUpdateContext.Provider>
  )
}
