import React, { useState } from 'react'
import Link from '../../Link'
import {
  makeStyles,
  SwipeableDrawer,
  IconButton,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  useTheme,
} from '@material-ui/core/'
import Collapse from '@material-ui/core/Collapse'
import MenuIcon from '@material-ui/icons/Menu'
import SendIcon from '@material-ui/icons/Send'
import HomeIcon from '@material-ui/icons/Home'
import AppsIcon from '@material-ui/icons/Apps'
import AllInclusiveIcon from '@material-ui/icons/AllInclusive'
import InfoIcon from '@material-ui/icons/Info'
import AccountBalanceWalletIcon from '@material-ui/icons/AccountBalanceWallet'
import GroupIcon from '@material-ui/icons/Group'
import BusinessIcon from '@material-ui/icons/Business'
import EmojiEventsIcon from '@material-ui/icons/EmojiEvents'
import ExpandLess from '@material-ui/icons/ExpandLess'
import ExpandMore from '@material-ui/icons/ExpandMore'
import { usePagesContext } from '../../contexts/PagesContext'
import DarkModeListSwitch from '../DarkModeListSwitch'
import { event as GAevent } from '../../functions/gtag'
import { useLanguageContext } from '../../contexts/LangContext'
import LanguageListItem from '../LanguageListItem'

// const iconMapping = {
//   SendIcon,HomeIcon,AppsIcon,AllInclusiveIcon,InfoIcon,AccountBalanceWalletIcon,GroupIcon,BusinessIcon,EmojiEventsIcon
// }

const iconMapping = {
  SendIcon: <SendIcon />,
  HomeIcon: <HomeIcon />,
  AppsIcon: <AppsIcon />,
  AllInclusiveIcon: <AllInclusiveIcon />,
  InfoIcon: <InfoIcon />,
  AccountBalanceWalletIcon: <AccountBalanceWalletIcon />,
  GroupIcon: <GroupIcon />,
  BusinessIcon: <BusinessIcon />,
  EmojiEventsIcon: <EmojiEventsIcon />,
}

const useStyles = makeStyles(theme => ({
  toolbarMargin: {
    ...theme.mixins.toolbar,
    marginBottom: '3em',
  },
  menuIconContainer: {
    marginLeft: 'auto',
    '&:hover': {
      backgroundColor: 'transparent',
    },
  },
  drawer: {
    backgroundColor: theme.palette.primary.main,
    color: '#fafafa',
  },
  drawerItem: {
    ...theme.typography.tab,
    opacity: 0.7,
  },
  drawerItemSelected: {
    '& .MuiListItemText-root': {
      opacity: 1,
    },
  },
  specialDrawerItem: {
    backgroundColor: theme.palette.secondary.main,
  },
  drawerIcon: {
    height: '40px',
    width: '40px',
    color: '#fafafa',
  },
  drawerTextIcon: {
    color: '#fafafa',
  },
  nested: {
    paddingLeft: theme.spacing(4),
  },
}))

const DrawerMenu = props => {
  const [menuOpen, setMenuOpen] = useState(false)
  const [drawerOpen, setDrawerOpen] = useState(false)
  const theme = useTheme()
  const classes = useStyles()
  const {
    pages,
    currentPageIndecies,
    setCurrentPageIndecies,
  } = usePagesContext()

  const iOS = process.browser && /iPad|iPhone|iPod/.test(navigator.userAgent)

  const handleTabChange = (e, value) => {
    setCurrentPageIndecies([value, null])
  }
  const handleSubMenuClick = (event, parentIndex, index) => {
    setMenuOpen(false)
    setCurrentPageIndecies([parentIndex, index])
  }
  const { siteData, language } = useLanguageContext()
  return (
    <>
      <IconButton
        className={classes.menuIconContainer}
        onClick={() => setDrawerOpen(!drawerOpen)}
        disableRipple
      >
        <MenuIcon className={classes.drawerIcon} />
      </IconButton>
      <SwipeableDrawer
        disableBackdropTransition={!iOS}
        disableDiscovery={iOS}
        open={drawerOpen}
        onClose={() => setDrawerOpen(false)}
        onOpen={() => setDrawerOpen(true)}
        classes={{ paper: classes.drawer }}
      >
        <div className={classes.toolbarMargin}></div>
        <List
          component='nav'
          aria-labelledby='nested-list-subheader'
          className={classes.root}
          disablePadding
        >
          <LanguageListItem />
          <DarkModeListSwitch />
          {pages.map((page, index) => (
            <React.Fragment key={page.path}>
              <ListItem
                style={{direction: theme.direction}}
                divider
                button
                component={Link}
                href={page.path}
                selected={index === currentPageIndecies[0]}
                onClick={event => {
                  setDrawerOpen(false)
                  handleTabChange(event, index)
                  GAevent({
                    category: 'Menu Buttons',
                    action: `Mobile Drawer ${page.name} Click`,
                    label: 'Website Actions',
                    value: '0',
                  })
                }}
                className={page.special ? classes.specialDrawerItem : ''}
                classes={{ selected: classes.drawerItemSelected }}
              >
                <ListItemIcon className={classes.drawerTextIcon}>
                  {iconMapping[page.icon]}
                </ListItemIcon>
                <ListItemText className={classes.drawerItem} disableTypography>
                  {page.nameLang[language]}
                </ListItemText>
                {page.children.length > 0 ? (
                  menuOpen ? (
                    <IconButton
                      className={classes.menuIconContainer}
                      onClick={e => {
                        e.preventDefault()
                        e.stopPropagation()
                        setMenuOpen(false)
                      }}
                      disableRipple
                    >
                      <ExpandLess />
                    </IconButton>
                  ) : (
                    <IconButton
                      className={classes.menuIconContainer}
                      onClick={e => {
                        e.stopPropagation()
                        e.preventDefault()
                        setMenuOpen(true)
                      }}
                      disableRipple
                    >
                      <ExpandMore />
                    </IconButton>
                  )
                ) : null}
              </ListItem>
              {page.children.length > 0 ? (
                <Collapse in={menuOpen} timeout='auto' unmountOnExit>
                  <List component='div' disablePadding>
                    {page.children.map((childPage, childIndex) => (
                      <ListItem
                        divider
                        button
                        key={childPage.path}
                        component={Link}
                        href={childPage.path}
                        selected={
                          childIndex === currentPageIndecies[1] &&
                          index === currentPageIndecies[0]
                        }
                        onClick={event => {
                          setDrawerOpen(false)
                          handleTabChange(event, index)
                          handleSubMenuClick(event, index, childIndex)
                          GAevent({
                            category: 'Menu Buttons',
                            action: `Mobile Drawer ${childPage.name} Click`,
                            label: 'Website Actions',
                            value: '0',
                          })
                        }}
                        className={classes.nested}
                        classes={{ selected: classes.drawerItemSelected }}
                      >
                        <ListItemIcon className={classes.drawerTextIcon}>
                          {iconMapping[childPage.icon]}
                        </ListItemIcon>
                        <ListItemText
                          className={classes.drawerItem}
                          disableTypography
                        >
                          {childPage.nameLang[language]}
                        </ListItemText>
                      </ListItem>
                    ))}
                  </List>
                </Collapse>
              ) : null}
            </React.Fragment>
          ))}
        </List>
      </SwipeableDrawer>
    </>
  )
}
export default DrawerMenu
