import React, { useState } from 'react'
import { makeStyles } from '@material-ui/core/styles'
import { Icon, IconButton, useMediaQuery, useTheme } from '@material-ui/core'
import PlayCircleFilledIcon from '@material-ui/icons/PlayCircleFilled'
import { shadows } from '@material-ui/system'
const useStyles = makeStyles(theme => ({
  wrapper: {
    width: '100%',
    height: '315px',
    position: 'relative',
    objectFit: 'cover',
    backgroundColor: '#000',
  },
  videoThumb: {
    display: 'grid',
    justifyContent: 'center',
    alignContent: 'center',
    width: '100%',
    height: '315px',
    position: 'relative',
    backgroundSize: 'contain',
    objectFit: 'cover',
    backgroundRepeat: 'no-repeat',
    backgroundPosition: 'center',
  },
  playButton: {
    color: 'rgba(0,0,0,0.6)',
    fontSize: '6em',
    width: 'auto',
    cursor: 'pointer',
    '&:hover': {
      color: '#FF0000',
    },
  },
}))

const YoutubeContainer = props => {
  const classes = useStyles()
  const theme = useTheme()
  const [showVideo, setShowVideo] = useState(false)
  const handlePlay = () => {
    setShowVideo(true)
  }
  return (
    <div className={classes.wrapper}>
      {showVideo ? (
        <iframe
          title='Oculus Quest'
          width='560'
          height='315'
          src={`https://www.youtube.com/embed/${props.video}`}
          frameBorder='0'
          allow='accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture'
          allowFullScreen
          iv_load_policy='3'
          modestbranding='1'
          style={{ width: '100%' }}
        ></iframe>
      ) : (
        <div
          className={classes.videoThumb}
          style={{
            backgroundImage: `url(https://img.youtube.com/vi/${props.video}/mqdefault.jpg)`,
          }}
        >
          <Icon
            className={`fab fa-youtube ${classes.playButton}`}
            onClick={handlePlay}
          />
        </div>
      )}
    </div>
  )
}

export default YoutubeContainer
