import React from 'react'
import Button from '@material-ui/core/Button'
import Menu from '@material-ui/core/Menu'
import MenuItem from '@material-ui/core/MenuItem'
import { useLanguageContext } from '../contexts/LangContext'
import Link from '../Link'
const LanguageButton = props => {
  const [anchorEl, setAnchorEl] = React.useState(null)

  const { language, changeLanguageTo } = useLanguageContext()

  const handleClick = event => {
    setAnchorEl(event.currentTarget)
  }

  const handleClose = () => {
    setAnchorEl(null)
  }

  const handleSelect = event => {
    setAnchorEl(event.currentTarget.text)
    console.log(event.currentTarget)
  }

  return (
    <div>
      <Button
        aria-controls='simple-menu'
        aria-haspopup='true'
        onClick={handleClick}
      >
        Ln
      </Button>
      <Menu
        id='simple-menu'
        anchorEl={anchorEl}
        keepMounted
        open={Boolean(anchorEl)}
        onClose={handleClose}
        style={{ zIndex: '9999' }}
      >
        <MenuItem component={Link} onClick={handleSelect}>
          En
        </MenuItem>
        <MenuItem component={Link}>He</MenuItem>
      </Menu>
    </div>
  )
}

export default LanguageButton
