import React, { useEffect } from 'react'
import Head from 'next/Head'
import { Grid, makeStyles, Typography } from '@material-ui/core'
import { usePagesContext } from '../src/contexts/PagesContext'
import PersonBlock from '../src/ui/parts/PersonBlock'
import PageHeader from '../src/ui/PageHeader'

const managers = [
  {
    image: '/assets/idan.jpg',
    name: 'Idan Baron',
    title: 'Founder & CTO',
    text: 'Lorem ipsum bla bla',
  },
  {
    image: '/assets/lihoo.jpg',
    name: 'Lihoo Zaid',
    title: 'Founder & CEO',
    text: 'Lorem ipsum bla bla',
  },
]

const instructors = [
  {
    image: '/assets/dana.jpg',
    name: 'Dana Ganesh',
    title: 'Instructor',
    text: 'Lorem ipsum bla bla',
  },
  {
    image: '/assets/david.jpg',
    name: 'David Forman',
    title: 'Instructor',
    text: 'Lorem ipsum bla bla',
  },
  {
    image: '/assets/michael.jpg',
    name: 'Michael Strauss',
    title: 'Instructor',
    text: 'Lorem ipsum bla bla',
  },
  {
    image: '/assets/ossy.jpg',
    name: 'Ossy Biton',
    title: 'Instructor',
    text: 'Lorem ipsum bla bla',
  },
  {
    image: '/assets/ron.jpg',
    name: 'Ron Bauman',
    title: 'Instructor',
    text: 'Lorem ipsum bla bla',
  },
  {
    image: '/assets/tammy.jpg',
    name: 'Tammy Fine',
    title: 'Instructor',
    text: 'Lorem ipsum bla bla',
  },
]

const useStyles = makeStyles(theme => ({}))

const About = props => {
  const classes = useStyles()
  const { setPageIndecies, getPageMeta } = usePagesContext()

  useEffect(() => {
    setPageIndecies('/about')
  }, [])
  return (
    <>
      <Head>
        <title key='title'>About Us - Company & Team | VRFunTeam</title>
        <meta
          name='description'
          key='description'
          content='VRFunTeam provide imersive custom VR Experiences, led by talented instructors. Our team will ensure your trip trough the VR world will be imerssive and fun.'
        />
        <meta
          key='ogtitle'
          property='og:title'
          content='Better than reality VR experiences | About Us'
        />
        <meta
          key='ogurl'
          property='og:url'
          content='https://funteamvr.com/about'
        />
        <link
          rel='canonical'
          key='canonical'
          href='https://funteamvr.com/about'
        />
        <style
          dangerouslySetInnerHTML={{
            __html: `
            body { 
              min-height: 450px;
              height: 100vh;
              margin: 0;
              background: radial-gradient(circle, darken(dodgerblue, 10%), #1f4f96, #1b2949, #000);
            }
            
            .stage {
              height: 300px;
              width: 500px;
              margin: auto;
              position: absolute;
              top: 0; right: 0; bottom: 0; left: 0;
              perspective: 9999px;
              transform-style: preserve-3d;
            }
            
            .layer {
              width: 100%;
              height: 100%;
              position: absolute;
              transform-style: preserve-3d;
              animation: ಠ_ಠ 5s infinite alternate ease-in-out -7.5s;
              animation-fill-mode: forwards;
              transform: rotateY(40deg) rotateX(33deg) translateZ(0);
            }
            
            .layer:after {
              font: 150px/0.65 'Pacifico', 'Kaushan Script', Futura, 'Roboto', 'Trebuchet MS', Helvetica, sans-serif;
              content: '404;
              white-space: pre;
              text-align: center;
              height: 100%;
              width: 100%;
              position: absolute;
              top: 50px; 
              color: darken(#fff, 4%);
              letter-spacing: -2px;
              text-shadow: 4px 0 10px hsla(0, 0%, 0%, .13);
            }
            
            $i: 1;
            $NUM_LAYERS: 20;
            @for $i from 1 through $NUM_LAYERS {
              .layer:nth-child(#{$i}):after{
                transform: translateZ(($i - 1) * -1.5px);
              }
            }
            
            .layer:nth-child(n+#{round($NUM_LAYERS/2)}):after {
              -webkit-text-stroke: 3px hsla(0, 0%, 0%, .25);
            }
            
            .layer:nth-child(n+#{round($NUM_LAYERS/2 + 1)}):after {
              -webkit-text-stroke: 15px dodgerblue;
              text-shadow: 6px 0 6px darken(dodgerblue,35%),
                           5px 5px 5px darken(dodgerblue,40%),
                           0 6px 6px darken(dodgerblue,35%);
            }
            
            .layer:nth-child(n+#{round($NUM_LAYERS/2 + 2)}):after {
              -webkit-text-stroke: 15px darken(dodgerblue, 10%);
            }
            
            .layer:last-child:after {
              -webkit-text-stroke: 17px hsla(0, 0%, 0%, .1);
            }
            
            .layer:first-child:after{
              color: #fff;
              text-shadow: none;
            }
            
            @keyframes ಠ_ಠ {
              100% { transform: rotateY(-40deg) rotateX(-43deg); }
            }
            
            
             
                <style> 
                  `,
          }}
        ></style>
      </Head>
      <div class='stage'>
        <div class='layer'></div>
        <div class='layer'></div>
        <div class='layer'></div>
        <div class='layer'></div>
        <div class='layer'></div>
        <div class='layer'></div>
        <div class='layer'></div>
        <div class='layer'></div>
        <div class='layer'></div>
        <div class='layer'></div>
        <div class='layer'></div>
        <div class='layer'></div>
        <div class='layer'></div>
        <div class='layer'></div>
        <div class='layer'></div>
        <div class='layer'></div>
        <div class='layer'></div>
        <div class='layer'></div>
        <div class='layer'></div>
        <div class='layer'></div>
      </div>
    </>
  )
}

export default About
