import React, { useState } from 'react'
import { makeStyles } from '@material-ui/core/styles'
import { Icon, IconButton, useMediaQuery, useTheme } from '@material-ui/core'
import PlayCircleFilledIcon from '@material-ui/icons/PlayCircleFilled'

const useStyles = makeStyles(theme => ({
  wrapper: {
    width: '100%',
    height: '315px',
    position: 'relative',
  },
  videoThumb: {
    width: '560px',
    height: '315px',
    position: 'relative',
    backgroundSize: 'cover',
    objectFit: 'cover',
  },
}))

const YoutubeContainer = props => {
  const classes = useStyles()
  const theme = useTheme()
  const [showVideo, setShowVideo] = useState(false)
  const handlePlay = () => {
    setShowVideo(true)
  }
  return (
    <div className={classes.wrapper}>
      {showVideo ? (
        <iframe
          title='Oculus Quest'
          width='560'
          height='315'
          src={`https://www.youtube.com/embed/${props.video}`}
          frameBorder='0'
          allow='accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture'
          allowFullScreen
          iv_load_policy='3'
          modestbranding='1'
          style={{ width: '100%' }}
        ></iframe>
      ) : (
        <div
          className={classes.videoThumb}
          style={{
            backgroundImage: `url(https://img.youtube.com/vi/${props.video}/mqdefault.jpg)`,
          }}
        >
          <IconButton aria-label='play' onClick={handlePlay}>
            <PlayCircleFilledIcon fontSize='large' />
          </IconButton>
        </div>
      )}
    </div>
  )
}

export default YoutubeContainer
